var mongoose = require('mongoose');
var Schema = mongoose.Schema;
var bikeSchema = mongoose.Schema({
  name: String,
  username: { type: String, match: /[0-9]/ },
  phone: String,
  address: String,
  brand: String,
  zone: String,
  image:{ data: Buffer, contentType: String },
  state: { type: Number, default: 0 },
  createdtime: { type: Date, default: Date.now }
});
// 使用到两个不同的连接,使用createConnection,而不是connect
mongoose.createConnection('mongodb://qrbikeApplicant:APPLICANTqrbike@ds051368.mongolab.com:51368/node-qrbike');
module.exports = mongoose.model('bike', bikeSchema);